sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		gs.info("BP " + "rightnow: " + rightnow + "  testdate: " + testdate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate){
		gs.info("BP IDT In isdateToday" + strDate);

		var dateToday = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		var isTestDateToday = testdate.getLocalDate();
		var today = dateToday.getLocalDate();

		gs.info("BP IDT 2 " + "today: " + today + "  isTestDateToday: " + isTestDateToday);

		// Compare today and istoday to see if they are the same day
		if(isTestDateToday.compareTo(today) == 0){
			gs.info("BP IDT 2 returns TRUE!!");
			return true;
		}
		else {
			return false;
		}

	},

    type: 'NeedItUtils'
};
